package mixture;import dataView.*;public class MixturePosInfo implements PositionInfo {	public double x, y, z;		public MixturePosInfo(double x, double y, double z) {		this.x = x;		this.y = y;		this.z = z;	}		public boolean equals(PositionInfo otherPos) {		if (otherPos == null || !(otherPos instanceof MixturePosInfo))			return false;		MixturePosInfo other = (MixturePosInfo)otherPos;		return (x == other.x) && (y == other.y) && (z == other.z);	}}