package mixtureProg;import dataView.*;import mixture.*;public class MixConstraintsApplet extends DragMixtureApplet {			protected void createCoreVariables(DataSet data) {		data.addNumVariable("y", getParameter(Y_VAR_NAME_PARAM), getParameter(Y_VALUES_PARAM));		data.addNumVariable("x", getParameter(X_VAR_NAME_PARAM), getParameter(X_VALUES_PARAM));		data.addNumVariable("z", getParameter(Z_VAR_NAME_PARAM), getParameter(Z_VALUES_PARAM));	}		protected ConstrainedTriangleView createTriangleView(DataSet data) {		ConstrainedTriangleView theView = new ConstrainedTriangleView(data, this, "x", "y", "z");		theView.setRetainLastSelection(false);		return theView;	}	}