package models;import java.awt.*;import dataView.*;public class LSCoeffValue extends Value {	public NumValue coeff[];		public LSCoeffValue(NumValue[] coeff) {		this.coeff = coeff;	}	public String toString() {		String result = coeff[0].toString() + getVariableString(0);		for (int i=1 ; i<coeff.length ; i++)			result += "+" + coeff[i].toString() + getVariableString(i);		return result;	}		public void drawAtPoint(Graphics g, int x, int y) {		drawCentred(g, x, y);	}		protected String getVariableString(int i) {		return (i == 0) ? "" : "x" + i;	}}