package multiRegn;import dataView.*;public class XZDragPosInfo implements PositionInfo {	public XZDragPosInfo(double x, double z) {		this.x = x;		this.z = z;	}		public boolean equals(PositionInfo otherPos) {		if (otherPos == null || !(otherPos instanceof XZDragPosInfo))			return false;		XZDragPosInfo other = (XZDragPosInfo)otherPos;		return (x == other.x) & (z == other.z);	}		public double x, z;}