package multiRegnProg;import dataView.*;import graphics3D.*;import multiRegn.*;public class DragParamResidApplet extends DragParamsApplet {		protected DataSet readData() {		DataSet data = super.readData();				String yValues = getParameter(Y_VALUES_PARAM);		data.addNumVariable("y", getParameter(Y_VAR_NAME_PARAM), yValues);		String xValues = getParameter(X_VALUES_PARAM);		data.addNumVariable("x", getParameter(X_VAR_NAME_PARAM), xValues);		String zValues = getParameter(Z_VALUES_PARAM);		data.addNumVariable("z", getParameter(Z_VAR_NAME_PARAM), zValues);				return data;	}		protected Rotate3DView getDataView(DataSet data, D3Axis xAxis, D3Axis yAxis,																																		D3Axis zAxis, String modelKey) {		return new DragParamResidView(data, this, xAxis, yAxis, zAxis, "model", "y", "x", "z", equationView);	}}