package propnVenn;public class GridIterator {	private int x0, y0, width, height;	private boolean byRow;	public int x, y;		public GridIterator(int x0, int y0, int width, int height, boolean byRow) {		this.x0 = x0;		this.y0 = y0;		this.width = width;		this.height = height;		this.byRow = byRow;	}		public void reset() {		x = x0;		y = y0;	}		public void nextItem() {		if (byRow) {			x++;			if (x == x0 + width) {				x = x0;				y++;			}		}		else {			y++;			if (y == y0 + height) {				y = y0;				x++;			}		}	}		public boolean finished() {		return (y >= y0 + height) || (x >= x0 + width);	}}