package random;public class RandomUniform extends RandomDiscrete {	private int min, max;	private int mask;		public RandomUniform(int count, int min, int max) {		super(count);		this.min = min;		this.max = max;		int diff = max - min;		mask = 0;		int power = 1;		while (mask <= diff) {			mask = mask + power;			power <<= 1;		}	}		public int generateOne() {		int result;		int diff = max - min;		while (true) {			result = nextInt() & mask;			if (result <= diff)				break;		}		return min + result;	}}