package randomisation;import dataView.*;public class MeanMedDiffVariable extends NumSummaryVariable {	private int decimals;	private double median;	private String numKey;		public MeanMedDiffVariable(String theName, String numKey, double median, int decimals) {		super(theName);		this.median = median;		this.numKey = numKey;		this.decimals = decimals;	}		protected NumValue evaluateSummary(DataSet sourceData) {		double sum = 0.0;				NumVariable yVar = (NumVariable)sourceData.getVariable(numKey);		ValueEnumeration ye = yVar.values();		while (ye.hasMoreValues())			sum += ye.nextDouble();				return new NumValue(sum / yVar.noOfValues() - median, decimals);	}}