package regn;import dataView.*;import models.*;public class LSLinearModel extends LinearModel implements FunctionInterface {		private String yKey;		public LSLinearModel(String name, DataSet data, String xKey, String yKey, int intDecs,																	int slopeDecs, int sdDecs) {		super(name, data, xKey);		this.yKey = yKey;		setLSParams(yKey, intDecs, slopeDecs, sdDecs);	}		public boolean noteVariableChange(String key) {		boolean changedExplan = (key.equals(xKey[0]) || key.equals(yKey));		if (changedExplan)			updateLSParams(yKey);		return changedExplan;	}			public void noteValueChange(int theIndex) {	}}