package simulation;import dataView.*;import distn.*;public class CoverageSummaryVariable extends NumSummaryVariable {	static final private int kDecimals = 3;		private String distnKey, dataKey;	private int intervalIndex;		public CoverageSummaryVariable(String theName, String distnKey, String dataKey,																					int intervalIndex) {		super(theName);		this.distnKey = distnKey;		this.dataKey = dataKey;		this.intervalIndex = intervalIndex;	}		protected NumValue evaluateSummary(DataSet sourceData) {		NormalDistnVariable distn = (NormalDistnVariable)sourceData.getVariable(distnKey);		NumVariable yVar = (NumVariable)sourceData.getVariable(dataKey);				NumValue sortedData[] = yVar.getSortedData();				double lowCum = 0.0;		double highCum = 1.0;		if (intervalIndex > 0)			lowCum = distn.getCumulativeProb(sortedData[intervalIndex - 1].toDouble());		if (intervalIndex < yVar.noOfValues())			highCum = distn.getCumulativeProb(sortedData[intervalIndex].toDouble());				return new NumValue(highCum - lowCum, kDecimals);	}}