package simulation;import dataView.*;import distn.*;public class DistnFunctionVariable extends NumFunctionVariable {	private NumVariable rectVar;	private ContinDistnVariable distn;	private String distnKey;	private int decimals;		public DistnFunctionVariable(String theName, DataSet data, String rectKey,																	String distnKey, int decimals) {		super(theName);		rectVar = (NumVariable)data.getVariable(rectKey);		distn = (ContinDistnVariable)data.getVariable(distnKey);		this.distnKey = distnKey;		this.decimals = decimals;	}		public int getMaxDecimals() {		return decimals;	}		public int noOfValues() {		return rectVar.noOfValues();	}		public Value valueAt(int index) {		double p = rectVar.doubleValueAt(index);		double y = distn.getQuantile(p);		return new NumValue(y, decimals);	}		public boolean noteVariableChange(String key) {		return distnKey.equals(key);	}}