package simulation;import dataView.*;public class PseudoDiscreteVariable extends NumSummaryVariable {	static final public int INDEX = 0;	static final public int VALUE = 1;		private String randomKey;	private int valueType;		public PseudoDiscreteVariable(String theName, String randomKey, int valueType) {		super(theName);		this.randomKey = randomKey;		this.valueType = valueType;	}		protected NumValue evaluateSummary(DataSet sourceData) {		OneRandomVariable oneRandom = (OneRandomVariable)sourceData.getVariable(randomKey);				if (valueType == INDEX)			return new NumValue(oneRandom.getIndex(), 0);		else			return oneRandom.getValue();	}}