package simulation;import dataView.*;public class PseudoRandomVariable extends NumVariable {	private int seedDigits;	private int modValue, plus, times;		public PseudoRandomVariable(String theName, long seed, int seedDigits,																				int plus, int times) {		super(theName);		resetGenerator(seed, seedDigits, plus, times);	}		public void resetGenerator(long seed, int seedDigits, int plus, int times) {		clearData();		this.seedDigits = seedDigits;		modValue = 1;		for (int i=0 ; i<seedDigits ; i++)			modValue *= 10;		this.plus = plus;		this.times = times;		addValue(new NumValue(seed, 0));	}		public void resetGenerator() {		long lastSeed = Math.round(doubleValueAt(noOfValues() - 1));		clearData();		addValue(new NumValue(lastSeed, 0));	}		public void generateNext() {		long lastSeed = Math.round(doubleValueAt(noOfValues() - 1));		long nextSeed = (lastSeed * times + plus) % modValue;		addValue(new NumValue(nextSeed, 0));	}		public int getTimes() {		return times;	}		public int getPlus() {		return plus;	}		public int getSeedDigits() {		return seedDigits;	}		public long calcBeforeMod(long seed) {		return seed * times + plus;	}}