package simulation;import dataView.*;import distn.*;public class QuantileFunctionVariable extends NumFunctionVariable {	private int noOfQuantiles;	private ContinDistnVariable distn;	private int decimals;		public QuantileFunctionVariable(String theName, ContinDistnVariable distn,																			int noOfQuantiles, int decimals) {		super(theName);		this.distn = distn;		this.noOfQuantiles = noOfQuantiles;		this.decimals = decimals;	}		public void setNoOfQuantiles(int noOfQuantiles) {		this.noOfQuantiles = noOfQuantiles;	}		public int getMaxDecimals() {		return decimals;	}		public int noOfValues() {		return noOfQuantiles;	}		public Value valueAt(int index) {		double q = distn.getQuantile((index + 1.0) / (noOfQuantiles + 1.0));		return new NumValue(q, decimals);	}}