package simulation;import dataView.*;public class RankOneVariable extends CatFunctionVariable {	private NumVariable rawVariable;	private String rawKey;		public RankOneVariable(String theName, DataSet data, String rawKey,																			String labelStrings) {		super(theName);		readLabels(labelStrings);		rawVariable = (NumVariable)data.getVariable(rawKey);		this.rawKey = rawKey;	}		public int noOfValues() {		return rawVariable.noOfValues();	}		public Value valueAt(int index) {		double rank = rawVariable.doubleValueAt(index);		return getLabel((rank < 1.5) ? 0 : 1);	}		public boolean noteVariableChange(String key) {		return rawKey.equals(key);	}}