package simulation;import java.util.*;import dataView.*;public class U01RandomVariable extends NumVariable {	private Random generator = new Random();	private int decimals;		public U01RandomVariable(String theName, int decimals) {		super(theName);		this.decimals = decimals;	}		public void resetGenerator() {		double lastValue = doubleValueAt(noOfValues() - 1);		clearData();		addValue(new NumValue(lastValue, decimals));	}		public void generateNext() {		addValue(new NumValue(generator.nextDouble(), decimals));	}}