package simulation;import dataView.*;import sport.*;public class WinProbAVariable extends NumSummaryVariable {	private String resultsKey;	private int decimals;		public WinProbAVariable(String theName, String resultsKey, int decimals) {		super(theName);		this.resultsKey = resultsKey;		this.decimals = decimals;	}		protected NumValue evaluateSummary(DataSet sourceData) {		LeagueResultsVariable results = (LeagueResultsVariable)sourceData.getVariable(resultsKey);				return new NumValue(results.getTeam0Prob(), decimals);	}}