package sport;import dataView.*;public class MinimumVariable extends NumSummaryVariable {	private String sourceKey, reportingKey;		public MinimumVariable(String theName, String sourceKey, String reportingKey) {		super(theName);		this.sourceKey = sourceKey;		this.reportingKey = reportingKey;	}		protected NumValue evaluateSummary(DataSet sourceData) {		double minValue = Double.POSITIVE_INFINITY;		NumValue minReported = null;				NumVariable variable = (NumVariable)sourceData.getVariable(sourceKey);		NumVariable reportVariable = (NumVariable)sourceData.getVariable(reportingKey);				int nVals = variable.noOfValues();		for (int i=0 ; i<nVals ; i++) {			double val = variable.doubleValueAt(i);			if (val < minValue) {				minValue = val;				minReported = (NumValue)reportVariable.valueAt(i);			}		}				return new NumValue(minReported);	}}