package sport;import dataView.*;public class PointSpreadVariable extends NumSummaryVariable {	static final public int RANGE = 0;	static final public int SD = 1;		private String resultsKey;	private int summaryType;		public PointSpreadVariable(String theName, String resultsKey, int summaryType) {		super(theName);		this.resultsKey = resultsKey;		this.summaryType = summaryType;	}		protected NumValue evaluateSummary(DataSet sourceData) {		LeagueResultsVariable results = (LeagueResultsVariable)sourceData.getVariable(resultsKey);				double[] finalPoints = results.getFinalPoints();		if (summaryType == RANGE) {			double minPoints = finalPoints[0];			double maxPoints = finalPoints[0];			for (int i=1 ; i<finalPoints.length ; i++)				if (finalPoints[i] > maxPoints)					maxPoints = finalPoints[i];				else if (finalPoints[i] < minPoints)					minPoints = finalPoints[i];						return new NumValue(maxPoints - minPoints, 0);		}		else {			int sx = 0;			int sxx = 0;			for (int i=0 ; i<finalPoints.length ; i++) {				sx += finalPoints[i];				sxx += finalPoints[i] * finalPoints[i];			}			double sd = Math.sqrt((sxx - sx * sx / (double)finalPoints.length) / (finalPoints.length - 1));			return new NumValue(sd, 2);		}	}}