package sport;import java.util.*;public class RandomPoint extends Random {	private double probServerWins[] = new double[2];		public RandomPoint(String randomSeed, double pAWinsServe, double pBWinsServe) {		if (randomSeed != null) {			setSeed((Long.valueOf(randomSeed)).longValue());			setServerWinProbs(pAWinsServe, pBWinsServe);		}	}		public RandomPoint(double pAWinsServe, double pBWinsServe) {		this(null, pAWinsServe, pBWinsServe);	}		public int nextWinner(int server) {		double rand = nextDouble();		return (rand < probServerWins[server]) ? server : (1 - server);	}		public void setServerWinProbs(double pAWinsServe, double pBWinsServe) {		probServerWins[0] = pAWinsServe;		probServerWins[1] = pBWinsServe;	}}