package sport;import java.util.*;public class RandomResult extends Random {	static final public int LOSE_SCORE = 0;	static final public int DRAW_SCORE = 1;	static final public int WIN_SCORE = 3;		private double probs[] = new double[2];		public RandomResult(String randomSeed) {		if (randomSeed != null) {			setSeed((Long.valueOf(randomSeed)).longValue());		}	}		public RandomResult() {		this(null);	}		public void generateRound(int[][] result, LeagueResultsVariable resultsVar) {		int noOfTeams = result.length;		for (int i=0 ; i<noOfTeams ; i++)			for (int j=0 ; j<noOfTeams ; j++)				if (i == j)					result[i][j] = 0;				else {					resultsVar.setResultProbs(i, j, probs);					double pWin = probs[0];					double pDraw = probs[1];					double rand = nextDouble();					result[i][j] = (rand < pWin) ? WIN_SCORE										: (rand < pWin + pDraw) ? DRAW_SCORE										: LOSE_SCORE;				}	}		public boolean homeTeamWins(int homeIndex, int awayIndex, LeagueResultsVariable resultsVar) {		resultsVar.setResultProbs(homeIndex, awayIndex, probs);		double pHomeWin = probs[0] / (1.0 - probs[1]);		return nextDouble() < pHomeWin;	}}