package sport;import dataView.*;public class RecordsVariable extends NumSummaryVariable {	private String winningTimeKey;		public RecordsVariable(String theName, String winningTimeKey) {		super(theName);		this.winningTimeKey = winningTimeKey;	}		protected NumValue evaluateSummary(DataSet sourceData) {		double record = Double.POSITIVE_INFINITY;		int nRecords = 0;				NumVariable variable = (NumVariable)sourceData.getVariable(winningTimeKey);				int nVals = variable.noOfValues();		for (int i=0 ; i<nVals ; i++) {			double val = variable.doubleValueAt(i);			if (val < record) {				record = val;				nRecords ++;			}		}				return new NumValue(nRecords, 0);	}}