package sport;import java.util.*;import dataView.*;import distn.*;public class ScaledNormalDistnVariable extends NormalDistnVariable {	private double sdIntercept, sdSlope;		public ScaledNormalDistnVariable(String theName, String scalingParam) {		super(theName);				StringTokenizer st = new StringTokenizer(scalingParam);		sdIntercept = Double.parseDouble(st.nextToken());		sdSlope = Double.parseDouble(st.nextToken());		int sdDecimals = Integer.parseInt(st.nextToken());		sd.decimals = sdDecimals;	}		protected double findSD(double mean) {		return sdIntercept + sdSlope * mean;	}		public void setMean(double newMean) {		mean.setValue(newMean);		setSD(findSD(newMean));	}		public void setMean(NumValue newMean) {		setMean(newMean.toDouble());		mean.decimals = newMean.decimals;	}}