package sport;public class TableColumnEnumeration {	private int noOfCols, nextColIndex;	private int nextColumnCentre, colIncrement;		public TableColumnEnumeration(int tableLeft, int minResultWidth,																int valueExtraWidth, int noOfCols) {		this.noOfCols = noOfCols;		nextColIndex = 0;		nextColumnCentre = tableLeft + CoreLeagueView.kTableTopBottomBorder + 1																+ (minResultWidth + valueExtraWidth) / 2;		colIncrement = minResultWidth + valueExtraWidth + LeagueResultsView.kMinColGap;	}		public boolean hasMoreCols() {		return (nextColIndex < noOfCols);	}		public int nextColumnCentre() {		int columnCentre = nextColumnCentre;		nextColumnCentre += colIncrement;		nextColIndex ++;		return columnCentre;	}}