package sport;public class TableRowEnumeration {	private int noOfRows, nextRowIndex;	private int nextBaseline, rowIncrement;		public TableRowEnumeration(int tableTop, int ascent, int descent,																int valueExtraHeight, int noOfRows) {		this.noOfRows = noOfRows;		nextRowIndex = 0;		nextBaseline = tableTop + CoreLeagueView.kTableTopBottomBorder + 1 + ascent																						+ valueExtraHeight / 2;		rowIncrement = ascent + descent + valueExtraHeight + CoreLeagueView.kMinRowGap;	}		public boolean hasMoreRows() {		return (nextRowIndex < noOfRows);	}		public int nextRowBaseline() {		int baseline = nextBaseline;		nextBaseline += rowIncrement;		nextRowIndex ++;		return baseline;	}}