package sport;import dataView.*;public class TeamARankVariable extends NumSummaryVariable {	static final public int FROM_LEAGUE = 0;	static final public int FROM_FINALS = 1;		private String resultsKey;	private int competitionType;		public TeamARankVariable(String theName, String resultsKey, int competitionType) {		super(theName);		this.resultsKey = resultsKey;		this.competitionType = competitionType;	}		protected NumValue evaluateSummary(DataSet sourceData) {		LeagueResultsVariable results = (LeagueResultsVariable)sourceData.getVariable(resultsKey);				int[] indexFromRank = results.getRankIndices();				if (competitionType == FROM_LEAGUE) {			for (int i=0 ; i<indexFromRank.length ; i++)				if (indexFromRank[i] == 0)					return new NumValue(i+1, 0);		}		else {			int[] finalIndexFromRank = results.getFinalsRankIndices();			for (int i=0 ; i<4 ; i++)				if (finalIndexFromRank[i] == 0)					return new NumValue(i+1, 0);			for (int i=4 ; i<indexFromRank.length ; i++)				if (indexFromRank[i] == 0)					return new NumValue(i+1, 0);		}		return null;	}}