package statistic2;import dataView.*;import coreGraphics.*;class SpreadLimits {	SpreadLimits(double low, double high) {		this.low = low;		this.high = high;	}	double low, high;}abstract public class StatCalculator {	private int statistic;		public StatCalculator(int statistic) {		this.statistic = statistic;	}		abstract public double evaluateStat(NumVariable variable, BoxInfo theBoxInfo);		abstract protected SpreadLimits findSpreadLimits(NumVariable variable, BoxInfo theBoxInfo);		abstract public String getName(XApplet applet);		public void setStat(int newStat) {		this.statistic = newStat;	}		public int getStat() {		return statistic;	}}