package statistic2Prog;import axis.*;import dataView.*;import distn.*;import statistic2.*;public class NormalSDExerciseApplet extends HistoSDExerciseApplet {		protected DataSet getData() {		DataSet data = super.getData();						NormalDistnVariable normal = new NormalDistnVariable(getParameter(VAR_NAME_PARAM));		data.addVariable("normDistn", normal);		return data;	}		protected BasicDataView getDataView(DataSet data, MultiHorizAxis axis, int meanDecimals,																														int sdDecimals) {		return new BasicNormalView(data, this, axis, null, meanDecimals, sdDecimals, "normDistn");	}	}