package statisticProg;import statistic.*;public class AbsDiffApplet extends DragAxisApplet {	protected Statistic getLowStatistic() {		return new AbsDiffStatistic(Statistic.LOW, Statistic.SHOW_GRAPH, Statistic.SHOW_EQN, this);	}		protected Statistic getHighStatistic() {		return new AbsDiffStatistic(Statistic.HIGH, Statistic.SHOW_GRAPH, Statistic.SHOW_EQN, this);	}		protected Statistic getTotalStatistic() {		return new AbsDiffStatistic(Statistic.ALL, Statistic.SHOW_GRAPH, Statistic.SHOW_EQN, this);	}}