package stdError;import dataView.*;import percentile.*;public class PercentileVariable extends NumSummaryVariable {	private String sourceKey;	private int decimals;	private double prob;		public PercentileVariable(String theName, String sourceKey, double prob, int decimals) {		super(theName);		this.sourceKey = sourceKey;		this.decimals = decimals;		this.prob = prob;	}		protected NumValue evaluateSummary(DataSet sourceData) {		NumVariable variable = (NumVariable)sourceData.getVariable(sourceKey);		NumValue sortedVal[] = variable.getSortedData();		double percentile = PercentileInfo.evaluatePercentile(sortedVal, prob,																																		PercentileInfo.SMOOTH);		return new NumValue(percentile, decimals);	}}