package stdError;import dataView.*;public class PropnBiggerVariable extends NumSummaryVariable {	private String sourceKey;	private int decimals;	private double cutoff;		public PropnBiggerVariable(String theName, String sourceKey, double cutoff, int decimals) {		super(theName);		this.sourceKey = sourceKey;		this.cutoff = cutoff;		this.decimals = decimals;	}		protected NumValue evaluateSummary(DataSet sourceData) {		NumVariable yVar = (NumVariable)sourceData.getVariable(sourceKey);		ValueEnumeration ye = yVar.values();		int n = 0;		int nGreater = 0;		while (ye.hasMoreValues()) {			double y = ye.nextDouble();			if (y > cutoff)				nGreater ++;			n ++;		}				return new NumValue(nGreater / (double)n, decimals);	}}