package stdErrorProg;import dataView.*;import axis.*;import coreGraphics.*;import percentile.*;import stdError.*;public class ErrorBootstrapApplet extends ErrorSampleApplet {		protected DataSet getData() {		DataSet data = new DataSet();				NumVariable popn = new NumVariable(getParameter(VAR_NAME_PARAM));		popn.readValues(getParameter(VALUES_PARAM));		data.addVariable("popn", popn);				BootstrapNumVariable bootstrapVar = new BootstrapNumVariable(popn.name);		bootstrapVar.readValues(getParameter(VALUES_PARAM));		data.addVariable("y", bootstrapVar);				return data;	}		protected NumValue getTarget(DataSet data, double targetProb, int decimals) {		NumVariable variable = (NumVariable)data.getVariable("popn");		NumValue sortedVal[] = variable.getSortedData();		double percentile = PercentileInfo.evaluatePercentile(sortedVal, targetProb,																																		PercentileInfo.SMOOTH);		return new NumValue(percentile, decimals);	}		protected StackedPlusNormalView getDataView(DataSet data, SummaryDataSet summaryData,														HorizAxis horizAxis, String paramName, String statisticName) {		ParamAndStatView theView = new ParamAndStatView(data, this, horizAxis, null, "y", summaryData,																								"est", "error", target, paramName, statisticName);		theView.setShowDensity(ParamAndStatView.NO_DISTN);		theView.setActiveNumVariable("popn");		theView.setCrossSize(DataView.LARGE_CROSS);		return theView;	}}