package survey;import java.awt.*;import dataView.*;public class WaitThread extends Thread {	static final private int kDelaySecs = 1;	static final private String kWaitString = "Wait...";	private Font waitFont;		private DataView theView;		public WaitThread(DataView theView) {		this.theView = theView;		waitFont = new Font(XApplet.FONT, Font.BOLD, 24);		start();	}		public void run() {		try {			sleep(kDelaySecs * 1000);						Graphics g = theView.getGraphics();			g.setFont(waitFont);			FontMetrics fm = g.getFontMetrics();			g.drawString(kWaitString, (theView.getSize().width - fm.stringWidth(kWaitString)) / 2,										(theView.getSize().height + fm.getAscent() - fm.getDescent()) / 2);		} catch (InterruptedException e) {		}	}}