package test;import dataView.*;import qnUtils.*;public class PValueVariable extends NumSummaryVariable {	private HypothesisTest test;	private int decimals;		public PValueVariable(String theName, HypothesisTest test, int decimals) {		super(theName);		this.test = test;		this.decimals = decimals;	}		protected NumValue evaluateSummary(DataSet sourceData) {		double pValue = test.evaluatePValue();		return new NumValue(pValue, decimals);	}		public void setTest(HypothesisTest test) {		this.test = test;	}}