package time;import dataView.*;class AREnumeration implements ValueEnumeration {	private ValueEnumeration e;	private double serialCorr, const2;	private double previous;	private boolean initialised = false;		public AREnumeration(ValueEnumeration e, double serialCorr) {		this.e = e;		this.serialCorr = serialCorr;		this.const2 = Math.sqrt(1 - serialCorr * serialCorr);	}		public boolean hasMoreValues() {		return e.hasMoreValues();	}		public double nextDouble() {		double nextRaw = e.nextDouble();		if (initialised) {			previous = serialCorr * previous + const2 * nextRaw;			return previous;		}		else {			previous = nextRaw;			initialised = true;			return nextRaw;		}	}		public Value nextValue() {		return new NumValue(nextDouble());	}		public RepeatValue nextGroup() {		return new RepeatValue(nextValue(), 1);	}}