package time;import dataView.*;class ExpEnumeration implements ValueEnumeration {	private ValueEnumeration e;		public ExpEnumeration(ValueEnumeration e) {		this.e = e;	}		public boolean hasMoreValues() {		return e.hasMoreValues();	}		public double nextDouble() {		return Math.exp(e.nextDouble());	}		public Value nextValue() {		return new NumValue(nextDouble());	}		public RepeatValue nextGroup() {		return new RepeatValue(nextValue(), 1);	}}