package time;import dataView.*;public class ExpForecastVariable extends ExpSmoothVariable {	private int lag;		public ExpForecastVariable(String theName, DataSet data, String sourceKey, int lag) {		super(theName, data, sourceKey);		this.lag = lag;	}		public void setLag(int lag) {		int lagChange = lag - this.lag;		this.lag = lag;		markChanged(lagChange);	}		public Value valueAt(int index) {		if (index >= lag)			return super.valueAt(index - lag);		else			return new NumValue(Double.NaN);	}	protected int getMinInfluence(int index) {		return (index < lag) ? -1 : super.getMinInfluence(index - lag);	}		protected int getMaxInfluence(int index) {		return (index < lag) ? -1 : index - lag;	}}