package time;import dataView.*;class ExpSmoothEnumeration implements ValueEnumeration {	private ValueEnumeration e;	private double a;	private double previous;	private boolean initialised = false;		public ExpSmoothEnumeration(ValueEnumeration e, double a) {		this.e = e;		this.a = a;	}		public boolean hasMoreValues() {		return e.hasMoreValues();	}		public double nextDouble() {		double nextRaw = e.nextDouble();		if (initialised) {			if (!Double.isNaN(nextRaw))				previous = a * nextRaw + (1 - a) * previous;			return previous;		}		else {			previous = nextRaw;			initialised = !Double.isNaN(nextRaw);			return nextRaw;		}	}		public Value nextValue() {		return new NumValue(nextDouble());	}		public RepeatValue nextGroup() {		return new RepeatValue(nextValue(), 1);	}}