package time;import dataView.*;public class TruncatedSeriesVariable extends NumFunctionVariable {	static final private NumValue kNaNValue = new NumValue("?");		private NumVariable baseVariable;	private int lastIndex;		public TruncatedSeriesVariable(String theName, NumVariable baseVariable, int lastIndex) {		super(theName);		this.baseVariable = baseVariable;		this.lastIndex = lastIndex;	}		public void setLastIndex(int lastIndex) {		this.lastIndex = lastIndex;	}		public int getMaxDecimals() {		return baseVariable.getMaxDecimals();	}		public int noOfValues() {		return baseVariable.noOfValues();	}		public Value valueAt(int index) {		return (index <= lastIndex) ? baseVariable.valueAt(index) : kNaNValue;	}}