package timeProg;import axis.*;import dataView.*;public class TransformTimeApplet extends BasicTimeApplet {	private TransformVertAxis theVertAxis;		protected DataSet readData() {		DataSet data = new DataSet();		data.addNumVariable("y", getParameter(VAR_NAME_PARAM), getParameter(VALUES_PARAM));		return data;	}		protected String getCrossKey() {		return "y";	}		protected String[] getLineKeys() {		String keys[] = {"y"};		return keys;	}		protected boolean showDataValue() {		return true;	}		protected boolean showSmoothedValue() {		return false;	}		protected VertAxis vertAxis(DataSet data) {		theVertAxis = new TransformVertAxis(this);		theVertAxis.readNumLabels(getParameter(AXIS_INFO_PARAM));		return theVertAxis;	}		public void setupApplet() {		super.setupApplet();				theVertAxis.setLinkedData(getData(), true);	}}