package twoGroup;import java.awt.*;import dataView.*;import distn.TTable;import models.*;import coreSummaries.*;public class DiffCIVariable extends IntervalSummaryVariable {	private IntervalValue maxSummary;	private int decimals;	private double tValue;		public DiffCIVariable(String theName, double level, GroupsDataSet data, int decimals, IntervalValue maxSummary) {		super(theName);		int df = Math.min(data.getN(0), data.getN(1)) - 1;		this.tValue = TTable.quantile(0.5 + level * 0.5, df);		this.maxSummary = maxSummary;		this.decimals = decimals;	}		protected IntervalValue evaluateSummary(DataSet sourceData) {		GroupsDataSet anovaData = (GroupsDataSet)sourceData;		int n1 = anovaData.getN(0);		int n2 = anovaData.getN(1);		double m1 = anovaData.getMean(0);		double m2 = anovaData.getMean(1);		double s1 = anovaData.getSD(0);		double s2 = anovaData.getSD(1);				double diffSD = Math.sqrt(s1 * s1 / n1 + s2 * s2 / n2);				return new IntervalValue(m2 - m1, diffSD, tValue, decimals);	}		public int getMaxWidth(Graphics g, int decimals) {		return maxSummary.stringWidth(g);	}}