package twoGroup;import dataView.*;import models.*;public class TwoGroupPropnSummary extends NumSummaryVariable {	static final public int PROPN1 = 0;	static final public int PROPN2 = 1;	static final public int DIFFERENCE = 2;		private int paramType;	private int decimals;		public TwoGroupPropnSummary(String theName, int decimals, int paramType) {		super(theName);		this.decimals = decimals;		this.paramType = paramType;	}		protected NumValue evaluateSummary(DataSet sourceData) {		ContinTableDataSet continData = (ContinTableDataSet)sourceData;		double param = (paramType == PROPN1) ? continData.getPropn(0)							: (paramType == PROPN2) ? continData.getPropn(1)							: continData.getPropn(1) - continData.getPropn(0);		return new NumValue(param, decimals);	}}