package twoGroup;import dataView.*;import models.*;public class TwoGroupSummary extends NumSummaryVariable {	static final public int MEAN1 = 0;	static final public int MEAN2 = 1;	static final public int DIFFERENCE = 2;		private int paramType;	private int decimals;		public TwoGroupSummary(String theName, int decimals, int paramType) {		super(theName);		this.decimals = decimals;		this.paramType = paramType;	}		protected NumValue evaluateSummary(DataSet sourceData) {		GroupsDataSet anovaData = (GroupsDataSet)sourceData;		double param = (paramType == MEAN1) ? anovaData.getMean(0)							: (paramType == MEAN2) ? anovaData.getMean(1)							: anovaData.getMean(1) - anovaData.getMean(0);		return new NumValue(param, decimals);	}}