package utils;import java.awt.*;import dataView.*;public class IntegerSlider extends XSlider {		public IntegerSlider(String titleText, int minVal,											int maxVal, int startVal, int orientation, XApplet applet,											boolean addEquals) {		super(String.valueOf(minVal), String.valueOf(maxVal), titleText, minVal, maxVal, startVal, orientation,																																applet, addEquals);	}		protected Value translateValue(int val) {		return new NumValue(getValue(), 0);	}		protected int getMaxValueWidth(Graphics g) {		FontMetrics fm = g.getFontMetrics();		return Math.max(fm.stringWidth(String.valueOf(getMinValue())),															fm.stringWidth(String.valueOf(getMaxValue())));	}}