package utils;import java.awt.*;import java.util.*;public class StringLineTokenizer extends StringTokenizer {	private int pixWidth;	private FontMetrics fm;	private String lookAhead;	private int lookAheadLength;	private int spaceLength;		public StringLineTokenizer(String s, Graphics g, int pixWidth) {		super(s);		this.pixWidth = pixWidth;		fm = g.getFontMetrics();		spaceLength = fm.stringWidth(" ");		readNextString();	}		private void readNextString() {		lookAhead = hasMoreTokens() ? nextToken() : null;		if (lookAhead != null)			lookAheadLength = fm.stringWidth(lookAhead);	}		public boolean hasMoreLines() {		return lookAhead != null;	}		public String nextLine() {		if (lookAhead == null)			throw new NoSuchElementException();		StringBuffer sb = new StringBuffer(lookAhead);		int currentLength = lookAheadLength;		readNextString();		while (lookAhead != null && currentLength + lookAheadLength + spaceLength < pixWidth) {			sb.append(' ');			sb.append(lookAhead);			currentLength += (spaceLength + lookAheadLength);			readNextString();		}		return sb.toString();	}}