package utils;import java.awt.*;import java.awt.geom.*;import dataView.*;public class XVertLabel extends XLabel {		public XVertLabel(String theText, int alignment, XApplet applet) {		super(theText, alignment, applet);	}		protected void drawLabel(Graphics g) {		FontMetrics fm = g.getFontMetrics();				Graphics2D g2d = ((Graphics2D)g);		AffineTransform oldTransform = g2d.getTransform();		double ninetyDegrees = -Math.PI / 2;		g2d.translate(0, getSize().height);		g2d.rotate(ninetyDegrees);				int startVert = kBorder + fm.getAscent();		int startHoriz = kBorder;		if (alignment != LEFT) {			int labelWidth = fm.stringWidth(theText);			if (alignment == CENTER)				startHoriz = (getSize().height - labelWidth) / 2;			else				startHoriz = getSize().height - labelWidth;		}				g2d.drawString(getText(), startHoriz, startVert);				g2d.setTransform(oldTransform);	}		public Dimension getMinimumSize() {		Graphics g = getGraphics();		g.setFont(getFont());		FontMetrics fm = g.getFontMetrics();				int theHeight = 2 * kBorder + fm.stringWidth(theText);		int theWidth = 2 * kBorder + fm.getAscent() + fm.getDescent();				return new Dimension(theWidth, theHeight);	}}