package valueList;import dataView.*;public class ListPosInfo implements PositionInfo {	public ListPosInfo(int theItemIndex, boolean theTopNotBottom) {		itemIndex = theItemIndex;		topNotBottom = theTopNotBottom;	}		public boolean equals(PositionInfo otherPos) {		if (otherPos == null)			return false;		ListPosInfo other = (ListPosInfo)otherPos;		return itemIndex == other.itemIndex;	}		public int itemIndex;	public boolean topNotBottom;}