package varianceProg;import java.awt.*;import dataView.*;import models.*;import ssq.*;import variance.*;public class PureAdjustSsqApplet extends QuadAdjustSsqApplet {		protected CoreModelDataSet getData() {		PureRegnDataSet data = new PureRegnDataSet(this);				PureComponentVariable.addComponentsToData(data, "xNum", "y", "x",																				PureRegnDataSet.kLinLsKey, PureRegnDataSet.kFactorLsKey);				return data;	}		protected String[] componentKeys() {		return PureComponentVariable.kComponentKey;	}		protected Color[] componentColors() {		return PureComponentVariable.kComponentColor;	}		protected int[] componentTypes() {		return PureComponentVariable.kComponentType;	}		protected DataWithComponentsPanel scatterPanel(DataSet data) {		PureComponentsPanel thePanel = new PureComponentsPanel(this);		thePanel.setupPanel(data, "xNum", "x", "y", PureRegnDataSet.kLinLsKey,									PureRegnDataSet.kFactorLsKey, null, PureComponentVariable.TOTAL, this);		return thePanel;	}		protected Image[] ssqImages() {		AnovaImages.loadPureImages(this);		return AnovaImages.pureSsqs2;	}}